INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(138115, 'itIT', '', 'No, non beviamo sangue, quelli sono i San\'layn. Un genere totalmente diverso di Elfi emo.', 26365),
(138114, 'itIT', '', 'No.', 26365),
(138113, 'itIT', '', 'Mai!', 26365),
(138112, 'itIT', '', 'Non sono d\'accordo.', 26365),
(138111, 'itIT', '', 'Ricorda gli insegnamenti di Alleria.', 26365),
(138110, 'itIT', '', 'Cammina nell\'ombra.', 26365),
(138109, 'itIT', '', 'Resisti ai sussurri.', 26365),
(138108, 'itIT', '', 'Mantieni sempre la concentrazione.', 26365),
(138107, 'itIT', '', 'Il Vuoto non  l\'unica cosa a essere insaziabile.', 26365),
(138106, 'itIT', '', 'Le mie avance non vanno mai... a Vuoto.', 26365),
(138105, 'itIT', '', 'Chi ha bisogno della Luce?  molto pi divertente al buio.', 26365),
(138104, 'itIT', '', 'C\' un vuoto nel mio cuore. Sei qui per riempirlo?', 26365),
(138103, 'itIT', '', 'Stai guardando la mia Forma del Vuoto?', 26365),
(138102, 'itIT', '', 'Avvicinati. I sussurri sono la mia specialit.', 26365),
(138101, 'itIT', '', 'Dicono che il Vuoto abbia fame. Iniziamo con un piccolo assaggio.', 26365),
(138100, 'itIT', '', 'Fate fuoco su di loro!', 26365),
(138099, 'itIT', '', 'Scatenate tutto ci che avete!', 26365),
(138098, 'itIT', '', 'Mi serve pi mana.', 26365),
(138097, 'itIT', '', 'Ho poco mana.', 26365),
(138096, 'itIT', '', 'Pericolo in arrivo.', 26365),
(138095, 'itIT', '', 'Volete darmi una mano?!?', 26365),
(138094, 'itIT', '', 'Aiutatemi!', 26365),
(138093, 'itIT', '', 'Tornate nell\'ombra!', 26365),
(138092, 'itIT', '', 'Tornate indietro!', 26365),
(138091, 'itIT', '', 'Ritirata!', 26365),
(138090, 'itIT', '', 'Non abbiamo paura dell\'ignoto.', 26365),
(138089, 'itIT', '', 'La via per il potere  chiara.', 26365),
(138088, 'itIT', '', 'Il Vuoto ci chiama.', 26365),
(138087, 'itIT', '', 'Salve.', 26365),
(138086, 'itIT', '', 'Mi servono delle cure!', 26365),
(138085, 'itIT', '', 'Curami!', 26365),
(138084, 'itIT', '', 'Vieni con me.', 26365),
(138083, 'itIT', '', 'Ti guido io.', 26365),
(138082, 'itIT', '', 'Da questa parte.', 26365),
(138081, 'itIT', '', 'Colpite il mio bersaglio!', 26365),
(138080, 'itIT', '', 'Aiutatemi!', 26365),
(138079, 'itIT', '', 'Attaccate il mio nemico!', 26365),
(138078, 'itIT', '', 'Non posso che congratularmi.', 26365),
(138077, 'itIT', '', 'Notevole.', 26365),
(138076, 'itIT', '', 'Ben fatto.', 26365),
(137688, 'itIT', 'Ma i fiori chi si muovono non sono sempre malvagi! Vogliono solo mangiare insetti.', '', 26365),
(137687, 'itIT', 'Non tutti i fiori sono semplici fiori.', '', 26365),
(137686, 'itIT', 'T\'paartos  gi venuto in questo posto quando non doveva farlo.', '', 26365),
(137685, 'itIT', 'Alla fine, T\'paartos ha deciso di non avere pi paura e ha lasciato la caverna.', '', 26365),
(137684, 'itIT', 'Persino le ombre sembravano mostri per T\'paartos.', '', 26365),
(137683, 'itIT', 'T\'paartos ha corso e corso. Si  perso per giorni.', '', 26365),
(137682, 'itIT', 'T\'paartos ricorda un posto del genere. Quella volta che aveva scavato troppo a fondo.', '', 26365),
(137681, 'itIT', 'T\'paartos amava scavare e trovare cose.', '', 26365),
(137680, 'itIT', 'Quando T\'paartos ha avuto paura, ha trovato un posto per nascondersi.', '', 26365),
(137679, 'itIT', 'T\'paartos amava parlare agli animali. Lo ascoltavano con interesse.', '', 26365),
(137524, 'itIT', '', 'Nah, lasciate che ciccio giochi con le sue rane.', 26365),
(137522, 'itIT', 'Dici che dovremmo seguirlo?', '', 26365),
(137521, 'itIT', 'Ahahah! Che POPPANTE!', '', 26365),
(137520, 'itIT', '', 'Ohhh, palla di lardo sta piangendo!', 26365),
(137519, 'itIT', 'Il grassone non ha capito la domanda perch  STUPIDO!', '', 26365),
(137518, 'itIT', '', 'Come ha fatto un piccoletto come te a diventare cos GRASSO, T\'paartos?', 26365),
(137514, 'itIT', 'Vieni. T\'paartos crede di sapere cosa potrebbe fare il piccolo T\'paartos.', '', 26365),
(137513, 'itIT', 'T\'paartos si  perso per giorni.', '', 26365),
(137512, 'itIT', 'T\'paartos ricorda un posto del genere...', '', 26365),
(137471, 'itIT', 'Gli altri bambini non erano... sempre gentili con T\'paartos.', '', 26365),
(137470, 'itIT', 'T\'paartos ha visto il tuo cuore sul campo di battaglia. Il tuo coraggio  impareggiabile agli occhi di T\'paartos.', '', 26365),
(137469, 'itIT', 'Devo dirti una cosa. T\'paartos non ti ha scelto per la tua insignificanza.', '', 26365),
(137126, 'itIT', '', '', 26365),
(137125, 'itIT', '', '', 26365),
(137124, 'itIT', '', '', 26365),
(137123, 'itIT', '', '', 26365),
(136659, 'itIT', 'Cos\' questo posto?', 'Cos\' questo posto?', 26365),
(136658, 'itIT', 'Sono stati necessari anni per comprendere i poteri che il Vuoto nasconde. Ora siamo al culmine di tale conoscenza.', '', 26365),
(136631, 'itIT', '', 'Da questa parte.', 26365),
(136630, 'itIT', '', 'Faremo il possibile per trovarli, maest.', 26365),
(136629, 'itIT', 'Dama Ventolesto, inizia pure la ricerca del tuo popolo esiliato. Forse potresti accettare l\'aiuto del nostro campione.', '', 26365),
(136628, 'itIT', 'Grazie a entrambi per il vostro consiglio. Ora non rimane che decidere su quali potenziali alleati dovremmo concentrarci con priorit.', '', 26365),
(136627, 'itIT', 'Roccavento ha sofferto per mano di coloro che hanno ceduto ai sussurri dell\'ombra. Ma se altri riuscissero a controllare quel potere come te, sarebbero degli alleati formidabili.', '', 26365),
(136626, 'itIT', '', 'Ma ho scoperto l\'esistenza di un gruppo di Sin\'dorei esiliati per aver studiato il Vuoto come ho fatto io. Se riuscissi a trovarli, il loro sapere potrebbe aiutare la causa.', 26365),
(136625, 'itIT', '', 'Li ho contattati nella speranza di rivedere Lunargenta sotto la bandiera dell\'Alleanza. I miei sforzi sono stati vani.', 26365),
(136624, 'itIT', '', 'Maest, ricordo ancora quando la mia gente e la vostra combatterono insieme contro l\'Orda.', 26365),
(136623, 'itIT', 'Grazie, Gran Esarca. I soldati Forgialuce sarebbero una preziosa aggiunta per le nostre forze.', '', 26365),
(136622, 'itIT', 'Tra noi vi sono coraggiosi iniziati pronti a seguire il difficile cammino per diventare Forgialuce.', '', 26365),
(136621, 'itIT', 'Molti dei nostri soldati pi coraggiosi hanno dato la vita per la causa. Quelli rimasti sarebbero onorati di unirsi all\'Alleanza.', '', 26365),
(136620, 'itIT', 'La vittoria su Argus  stata possibile solo grazie agli eroi di Azeroth, che hanno combattuto al fianco dell\'Armata della Luce.', '', 26365),
(136619, 'itIT', 'Il nostro futuro dipende dal portare dalla nostra parte tutti coloro che la pensano come noi. Chiedo il vostro consiglio a riguardo.', '', 26365),
(136618, 'itIT', 'Nel frattempo, l\'Orda sta radunando le forze e si prepara alla guerra. La sua aggressione non pu passare inosservata.', '', 26365),
(136617, 'itIT', 'Sappiamo tutti che l\'Alleanza ha subito perdite ingenti nella guerra contro la Legione, e il mondo sanguina ancora a causa dell\'ultimo colpo inferto da Sargeras.', '', 26365),
(136616, 'itIT', 'Grazie di essere qui con noi. Sono tempi davvero duri questi.', '', 26365),
(136038, 'itIT', 'Le fessure di Azerite iniziano a eruttare nelle vicinanze!', '', 26365),
(135924, 'itIT', '', 'L\'Alleanza sta perlustrando i confini pi lontani di Azeroth in cerca di ogni possibile vantaggio contro di noi. Noi dobbiamo fare altrettanto.', 26365),
(135923, 'itIT', 'Sar fatto, Capoguerra.', '', 26365),
(135922, 'itIT', '', 'Capo Trib Zoccolo Sanguinario, fa\' in modo che il nostro campione si unisca al banchetto di Picco del Tuono. Voglio il loro giuramento all\'Orda entro la fine delle celebrazioni.', 26365),
(135921, 'itIT', '', 'Entrambe le scelte avrebbero i loro vantaggi. Qui per abbiamo qualcuno che conosce bene entrambi... su quale suggerisci di concentrarci prima?', 26365),
(135920, 'itIT', '', 'Anche senza il Pozzo Oscuro, Suramar possiede grandi risorse arcane. Una freccia in pi nella mia faretra.', 26365),
(135919, 'itIT', '', 'Il Reggente Theron e io crediamo che i nostri regni abbiano molto in comune. Secondo noi, i Nobili Oscuri sarebbero dei preziosi alleati.', 26365),
(135918, 'itIT', '', 'Anch\'io ho un suggerimento, Capoguerra. I Sin\'dorei hanno mantenuto legami diplomatici con i Nobili Oscuri sin da quando li abbiamo aiutati a liberare Suramar dalla Legione.', 26365),
(135917, 'itIT', '', ' gente semplice, ma forte. Sarebbero di grande aiuto sul campo di battaglia.', 26365),
(135916, 'itIT', 'Ho invitato i loro capi a un grande banchetto a Mulgore. Potrebbe essere l\'occasione giusta per convincerli a unirsi a noi.', '', 26365),
(135915, 'itIT', 'Capoguerra, i Tauren di Alto Monte hanno combattuto con coraggio al nostro fianco contro la Legione Infuocata, proprio come i nostri antenati fecero molto tempo fa.', '', 26365),
(135914, 'itIT', '', 'La guerra si avvicina. I ranghi dell\'Orda hanno bisogno di nuova linfa. Cosa ne pensate?', 26365),
(135913, 'itIT', '', 'Capi dell\'Orda, vi ho convocati qui, insieme a uno dei nostri pi grandi campioni, perch i nemici stanno radunando le forze.', 26365),
(135759, 'itIT', 'Non vedo motivo di rivelarlo ad altri, Spiritista. Lasciamo che la questione rimanga... tra amici.', '', 26365),
(135758, 'itIT', 'Solo Mayla e il nostro campione conoscevano il mio segreto. Ora lo affido anche a te, Capo Trib.', '', 26365),
(135756, 'itIT', 'Non credevo di aver invitato un drago a banchettare con noi a Picco del Tuono. Se l\'avessi saputo, avrei preparato un tavolo pi grande!', '', 26365),
(135751, 'itIT', 'Tu ci servirai, drago, proprio come fece Neltharion! Quelli della tua razza cedono sempre ai sussurri.', '', 26365),
(135748, 'itIT', 'Insieme, annienteremo la tua oscurit!', '', 26365),
(135747, 'itIT', 'Questi eroi non sono soli.', '', 26365),
(135746, 'itIT', 'Questa montagna appartiene a noi, mortali. Presto diverrete ricettacoli del vero potere di questo mondo!', '', 26365),
(135743, 'itIT', 'Che devastazione...', '', 26365),
(135742, 'itIT', 'Ho bisogno di parlare con te.', '', 26365),
(135739, 'itIT', 'Sar meglio che vada. Buona fortuna... al nostro prossimo incontro.', '', 26365),
(135738, 'itIT', 'Andr a Karazhan, invece,  per consultarmi col mio vecchio mentore, Medivh. Forse, da qualche parte nei suoi antichi testi, trover la cura per questo mondo.', '', 26365),
(135737, 'itIT', 'Ma io non prender parte a tutto questo, non dopo tutto quello che abbiamo passato.', '', 26365),
(135736, 'itIT', 'Speravo che dopo la guerra contro la Legione, i popoli di Azeroth avrebbero costruito insieme un futuro migliore. Ora per quel sogno sembra essersi infranto.', '', 26365),
(135735, 'itIT', 'La linfa vitale di Azeroth si riversa sul mondo, e i capi dell\'Alleanza e dell\'Orda cercano di impadronirsene per i propri fini. Venti di guerra si stanno alzando ancora una volta.', '', 26365),
(135734, 'itIT', 'Pare che la nostra vittoria su Argus non sia servita a nulla. Con il suo ultimo atto, Sargeras ha fatto ben pi che trafiggere il cuore del nostro mondo.', '', 26365),
(134778, 'itIT', 'Ah, accomodati se trovi una sedia!', '', 26365),
(134777, 'itIT', 'Ahahahah, sono felice di rivederti!', '', 26365),
(134776, 'itIT', '$GBentornato:Bentornata;!', '', 26365),
(134775, 'itIT', 'Avvicina la sedia al focolare!', '', 26365),
(134774, 'itIT', 'Ah, guarda un po\' chi c\'!', '', 26365),
(134773, 'itIT', 'Serata affollata... ma di sicuro ti troveremo un posto!', '', 26365),
(134772, 'itIT', 'Ti do il benvenuto nella mia locanda!', '', 26365),
(134771, 'itIT', 'Mi manca il Capoguerra. Manca a tutti noi.', '', 26365),
(134770, 'itIT', 'Nun sapevano che saremmo arrivati!', '', 26365),
(134769, 'itIT', 'Tranciacolle era gi perduta quando siamo arrivati.', '', 26365),
(134768, 'itIT', 'Ha fatto nu discorso cos accorato!', '', 26365),
(134767, 'itIT', 'Nun ci hanno nemmeno avvertiti. I Kor\'kron hanno attaccato Sen\'jin in forze e siamo stati fortunati che Vol\'jin fosse l ad aiutarci.', '', 26365),
(134761, 'itIT', 'Chi ha ordinato il pollo?', '', 26365),
(134122, 'itIT', '', 'Se la Luce  con te, allora hai una possibilit di salvare il tuo amico.$b$bMa non indugiare; se l\'influenza degli Dei Antichi non sar fermata, la sua mente cadr vittima della follia.', 26365),
(134118, 'itIT', '', 'Sento che porti notizie cupe.', 26365),
(134114, 'itIT', '$n ha recuperato dell\'Azerite!', '', 26365),
(134113, 'itIT', '$n ha recuperato dell\'Azerite!', '', 26365),
(134108, 'itIT', 'Servono protezioni potenti per tenere a bada gli Dei Antichi.\n\nForse un luogo difeso dalla magia del Vuoto potrebbe esserci utile.', '', 26365),
(134107, 'itIT', 'Gli spiriti sono irrequieti. Un\'ombra incombe su questo luogo. La terra stessa ha qualcosa... di sbagliato.', '', 26365),
(134106, 'itIT', '', 'Vorrei avere notizie migliori. Nella mia esperienza, una volta che gli Dei Antichi prendono il controllo di qualcosa, ogni speranza  perduta.', 26365),
(134105, 'itIT', '', 'A volte bisogna concentrarsi sui sogni per trovare la verit.', 26365),
(134104, 'itIT', 'La mente di un alleato  sotto l\'influenza degli Dei Antichi. Potr essere salvato?', 'La mente di un alleato  sotto l\'influenza degli Dei Antichi. Potr essere salvato?', 26365),
(134103, 'itIT', '', 'Che notizie porti? Percepisco che le ombre si addensano su questi picchi.', 26365),
(134097, 'itIT', 'Le fessure di Azerite iniziano a eruttare!', '', 26365),
(133035, 'itIT', 'Potrebbe essere gi troppo tardi! Pessime notizie. Riesco a sentire i canti rituali qui vicino.', '', 26365),
(133022, 'itIT', 'I Necronotte... Credevo che quei vili cultisti fossero stati spazzati via molto tempo fa.\n\n l\'occasione giusta per finire il lavoro. Posso condurci fino alla Volta di Neltharion.', '', 26365),
(127894, 'itIT', 'Non c\' bisogno di rintracciare il mittente. Ci penseranno i condotti!', '', 26365),
(127893, 'itIT', 'Ben fatto. Non possiamo permetterci di intasare il sistema con della posta inappropriata.', '', 26365),
(127870, 'itIT', 'Nessuna tempesta di neve, cataclisma o invasione demoniaca ci impedir di compiere il nostro dovere. la posta non si consegna da sola.', '', 26365),
(106684, 'itIT', 'Non far caso a Haramad. So che nel profondo apprezza tutta questa pietra gratuita.', '', 26365),
(106683, 'itIT', '', 'Oh, cielo, tutto questo non mi piace affatto. Vai e raggiungi il Postino, io penser a smantellare questo portale.', 26365),
(106681, 'itIT', 'Katy, aiuteresti $p ad aprire il vecchio portale del Consorzio vicino al Monumento ad Antonidas?', '', 26365),
(106677, 'itIT', '', 'Ci vediamo l!', 26365),
(106676, 'itIT', '', 'Non posso resistere per sempre! Fallo!', 26365),
(106675, 'itIT', 'La pagherai per questo. Ti trover... e ti distrugger.', '', 26365),
(106674, 'itIT', 'Cos\' questo? Ancora quella dannata pietra?!?', '', 26365),
(106625, 'itIT', 'Non farmi aspettare troppo, zotico che non sei altro. Su, su!', '', 26365),
(106583, 'itIT', ' un piacere fare affari con te. Hai appena dato una svolta alla mia carriera!', '', 26365),
(106582, 'itIT', 'Sfortunatamente... s.', 'Sfortunatamente... s.', 26365),
(106581, 'itIT', 'Oh, sei $gsicuro:sicura;? Non  un qualche tipo di scherzo?', '', 26365),
(106580, 'itIT', 'Devo... venderti questo cavallo.', 'Devo... venderti questo cavallo.', 26365),
(106567, 'itIT', 'Sono l\'Elfo che il tuo Elfo dovrebbe essere... se fosse me.', '', 26365),
(106521, 'itIT', 'Non puoi semplicemente darmelo?', '', 26365),
(106520, 'itIT', 'Non perder di nuovo!  mio!', '', 26365),
(106519, 'itIT', 'Hai idea di quanto a lungo ho atteso per questo?', '', 26365),
(106518, 'itIT', 'Non puoi capire. Ne ho bisogno!', '', 26365),
(106517, 'itIT', 'Mio.  mio! L\'ho detto prima io!', '', 26365),
(106516, 'itIT', 'L\'ho visto io per primo!', '', 26365);
